import React from "react";
import Form from "./Form.jsx";
import "./App.css";
import TodoList from "./TodoList.jsx";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      todos: [
        {
          title: "title",
          desc: "desc",
          isDone: false
        },
        {
          title: "title",
          desc: "desc",
          isDone: false
        }
      ]
    };
  }
  handleSubmit(e) {
    e.preventDefault();

    const title = e.target.title.value;
    const desc = e.target.title.value;
    const newTodos = this.state.todos.slice();
    newTodos.push({
      title: title,
      desc: desc,
      isDone: false
    });

    this.setState({
      todos: newTodos
    });
  }

  handleClick(i) {
    const newClicks = this.state.todos.slice();
    if (newClicks[i].isDone === false) {
      newClicks[i].isDOne = true;
    } else {
      newClicks[i].isDone = false;
    }

    this.setState({
      todos: newClicks
    });
  }
  render() {
    return (
      <React.Fragment>
        <Form handleSubmit={this.handleSubmit.bind(this)}></Form>
        <TodoList
          todos={this.state.todos}
          handleClick={this.handleClick.bind(this)}
        ></TodoList>
      </React.Fragment>
    );
  }
}

export default App;
