import React from 'react';
import Form from './Form.jsx';
import './App.css';
import TodoList from './TodoList.jsx';

class App extends React.Component {

  constructor() {
    super();
    this.state = {
      todos: [
        {
          title: 'title',
          desc: 'desc',
          isDone: false
        },
        {          
          title: 'title',
          desc: 'desc',
          isDone: false
        }
      ]
    }
  }
  render() {
    return(
      <React.Fragment>
        <Form></Form>
        <TodoList todos={this.state.todos}></TodoList>
      </React.Fragment>
    )
  }
}

export default App;
